import React,{ useEffect, useReducer, useState } from 'react'
import { getAll, getAllByTags, getAllTags, search } from '../../Services/FoodService'
import Thumbnails from '../../Components/Thumbnails/Thumbnails'
import classes from "./HomePage.module.css"
import { useParams } from 'react-router-dom'
import Search from '../../Components/Search/Search'
import Tags from '../../Components/Tags/Tags'
import NotFound from '../../Components/NotFound/NotFound'

const initialState = { foods : [], tags : [] }

const reducer = (state,action) => {
    switch(action.type){
        case 'FOODS_LOADED':
            return{
                ...state,
                foods : action.payload
            }
        case 'TAGS_LOADED':
          return{
            ...state,
            tags: action.payload
          }
        default:
            return state
    }
}

const HomePage = () => {
   
    const [state,dispatch] = useReducer(reducer,initialState)
    const { foods, tags } = state;
    const { searchTerm, tag, id } = useParams()

    useEffect(() => {
        setIsLoading(true);
        getAllTags()
        .then(tags => dispatch({type:'TAGS_LOADED',payload:tags}))
        .catch((error) => {
          console.error('Error loading Tags:', error);
        })
        
        const loadFoods = tag 
        ? getAllByTags(tag) 
        : searchTerm 
        ? search(searchTerm) 
        : getAll()  

        loadFoods
        .then((foods) => {
          dispatch({ type: 'FOODS_LOADED', payload: foods });
        })
        .catch((error) => {
          console.error('Error loading foods:', error);
        })
        .finally(() => {
          setIsLoading(false); 
        });

    },[searchTerm,tag,id])
   


  return (
    <div>
      <Search/>
      <Tags tags={tags}/>
      {isLoading ? (
        <div className={classes.loader}>
          <img className={classes.img} src='/pizza.png' alt='pizzaLoader' />
          <img className={classes.img2} src='/pie.png' alt='pie' />
        </div>
      ) :
      foods.length === 0 ? (<NotFound linkText="Reset Page"/>) : 
       ( <Thumbnails foods={foods}/> )
    }
    </div>
  )
}

export default HomePage
