import { useState, useContext, createContext } from "react";
import * as userService from "../Services/UserService";
import { toast } from "react-toastify";
import Cookies from "js";

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);

    // Check if there's a user stored in cookies on component mount
    useState(() => {
        const storedUser = userService.getUserFromCookies();
        if (storedUser) {
            setUser({ email: storedUser.email }); // Only store email in state
        }
    }, []);

    const register = async (email, password) => {
        try {
            await userService.register(email, password);
            toast.success('Registration successful');
        } catch (err) {
            toast.error(err.response.data);
        }
    };

    const login = async (email, password) => {
        try {
            const user = await userService.login(email, password);
            setUser({ email: user.email }); // Store only email in state
            toast.success('Login successful');
        } catch (err) {
            toast.error(err.response.data);
        }
    };

    const logout = async () => {
        try {
            await userService.logout();
            setUser(null);
            Cookies.remove('token');
            toast.success('Logout successful');
        } catch (err) {
            toast.error(err.response.data);
        }
    };

    return (
        <AuthContext.Provider value={{ register, login, logout, user }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
