import { useParams } from "react-router-dom"
import classes from "./FoodEditPage.module.css"
import { useForm } from "react-hook-form";
import { useEffect, useState } from "react";
import { getById } from "../../Services/FoodService";
import Title from "../../Components/Title/Title";
import InputContainer from "../../Components/InputContainer/InputContainer";
import Input from "../../Components/Input/Input";

const FoodEditPage = () => {
    const { foodId } = useParams();
    const [imageUrl, setImageUrl] = useState();
    const isEditMode = !!foodId;

    const{
      handleSubmit,
      register,
      formState: { errors },
      reset,
    } = useForm();

    useEffect(() => {
        if(!isEditMode) return;

        getById(foodId).then(food => {
            if(!food) return;
            reset(food);
            setImageUrl(food.imageUrl)
        })
    }, [foodId])

    const submit = () => {

    }

    const upload = () => {

    } 

  return (
    <div className={classes.container}>
      <div className={classes.content}>
         <Title title={isEditMode ? 'Edit Food' : 'Add Food'} />
         <form onSubmit={handleSubmit(submit)} noValidate>
            <InputContainer label="Select Image">
               <input type="file" onChange={upload} accept="image/jpeg"></input>
            </InputContainer>

            {imageUrl && 
            <a 
              href={`/pizzas/${imageUrl}`} 
              className={classes.image_link} 
              target="blank"
            >
               <img src={`/pizzas/${imageUrl}`} alt="uploaded" />   
            </a>
            }

            <Input type="text" label="Name" {...register('name', {required:true, minLength:5} )}
            error={errors.name}
            />
         </form>
      </div>
    </div>
  )
}

export default FoodEditPage
