import React from 'react';
import classes from "./Customize.module.css";
import { useCart } from '../../hooks/UseCart';
import { base } from '../../Data';

const Customize = ({ food, selectedToppings, setSelectedToppings, selectedBase, setSelectedBase }) => {
  const { addToCart } = useCart();
  const { veg } = useCart();

  const handleAddToCart = () => {
    addToCart(food, selectedToppings);
  };

  const handleToppingClick = (toppingPrice) => {
    if (selectedToppings.includes(toppingPrice)) {
      setSelectedToppings(selectedToppings.filter((topping) => topping !== toppingPrice));
      veg(food, -toppingPrice); // Assuming veg function subtracts the topping price
    } else {
      setSelectedToppings([...selectedToppings, toppingPrice]);
      veg(food, toppingPrice);
    }
  };

  const handleBaseClick = (basePrice) => {
    if (selectedBase === basePrice) {
      setSelectedBase(null);
    } else {
      setSelectedBase(basePrice);
    }
  };

  const isToppingSelected = (toppingPrice) => selectedToppings.includes(toppingPrice);

  return (
    <div className={classes.main}>
      <div className={classes.bases}>
        <label>Bases</label>
        {base.map(item => (
          <button
            key={item.id}
            value={item.price}
            onClick={() => handleBaseClick(item.price)}
            className={selectedBase === item.price ? classes.selected : ''}
          >
            {item.base} - ₹{item.price}
          </button>
        ))}
      </div>
      <div className={classes.veg}>
        <label>Veg </label>
        {food.toppings.Veg.map(item => (
          <button
            key={item.id}
            value={item.price}
            onClick={() => handleToppingClick(item.price)}
            disabled={isToppingSelected(item.price)}
            className={isToppingSelected(item.price) ? classes.selected : ''}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
        <label>NonVeg </label>
        {food.toppings.NonVeg.map(item => (
          <button
            key={item.id}
            value={item.price}
            onClick={() => handleToppingClick(item.price)}
            disabled={isToppingSelected(item.price)}
            className={isToppingSelected(item.price) ? classes.selected : ''}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
        <label>Cheese </label>
        {food.toppings.Cheese.map(item => (
          <button
            key={item.id}
            value={item.price}
            onClick={() => handleToppingClick(item.price)}
            disabled={isToppingSelected(item.price)}
            className={isToppingSelected(item.price) ? classes.selected : ''}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
      </div>
      <div>
        <button onClick={handleAddToCart}>Add Toppings</button>
      </div>
    </div>
  );
};

export default Customize;
