import React,{ useEffect, useReducer } from 'react'
import { getAll, getAllByTags, getAllTags, search } from '../../Services/FoodService'
import Thumbnails from '../../Components/Thumbnails/Thumbnails'
import classes from "./HomePage.module.css"
import { useParams } from 'react-router-dom'
import Search from '../../Components/Search/Search'
import Tags from '../../Components/Tags/Tags'
import NotFound from '../../Components/NotFound/NotFound'

const initialState = { foods : [], tags : [] }

const reducer = (state,action) => {
    switch(action.type){
        case 'FOODS_LOADED':
            return{
                ...state,
                foods : action.payload
            }
        case 'TAGS_LOADED':
          return{
            ...state,
            tags: action.payload
          }
        default:
            return state
    }
}

const HomePage = () => {
   
    const [state,dispatch] = useReducer(reducer,initialState)
    const { foods, tags } = state
    const { searchTerm, tag, id } = useParams()

    useEffect(() => {
      const fetchData = async () => {
        try {
          // Fetch tags and update state
          const fetchedTags = await getAllTags();
          dispatch({ type: 'TAGS_LOADED', payload: fetchedTags });
  
          // Determine which foods to fetch based on route parameters
          let fetchedFoods = [];
          if (tag) {
            fetchedFoods = await getAllByTags(tag);
          } else if (searchTerm) {
            fetchedFoods = await search(searchTerm);
          } else {
            fetchedFoods = await getAll();
          }
          dispatch({ type: 'FOODS_LOADED', payload: fetchedFoods });
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };
  
      fetchData();
    }, [searchTerm, tag, id]);
  

   

  return (
    <div>
      <Search/>
      <Tags tags={tags}/>
      {foods.length === 0 && <NotFound linkText="Reset Page"/>}
      <Thumbnails foods={foods}/>
      {/* <div className={classes.loader}>
           <img className={classes.img} src='/pizza.png' alt='pizzaLoader' />
           <img className={classes.img2} src='/pie.png' alt='pie' />
      </div> */}
    </div>
  )
}

export default HomePage
