import React, { createContext, useContext, useEffect, useState } from 'react';
import { base, toppingsCheese, toppingsNonVeg, toppingsVeg } from '../Data';

const CartContext = createContext(null);
const CART_KEY = 'cart';
const EMPTY_CART = {
  items: [],
  totalPrice: 0,
  totalCount: 0,
  topPrice: 0,
  crustPrice: 0
};

const CartProvider = ({ children }) => {
  const initCart = getCartFromLocalStorage();
  const [cartItems, setCartItems] = useState(initCart.items);
  const [totalPrice, setTotalPrice] = useState(initCart.totalPrice);
  const [totalCount, setTotalCount] = useState(initCart.totalCount);
  const [topPrice, setTopPrice] = useState(initCart.topPrice);
  const [crustPrice, setCrustPrice] = useState(initCart.crustPrice);

  const removeFromCart = (foodId) => {
    const filteredCartItems = cartItems.filter((item) => item.food.id !== foodId);
    setCartItems(filteredCartItems);
  };

  useEffect(() => {
    const totalPrice = sum(cartItems.map((item) => item.price));
    const totalCount = sum(cartItems.map((item) => item.quantity));
    const topPrice = sum(cartItems.map((item) => item.toppings));
    const crustPrice = sum(cartItems.map((item) => item.crust));
    setTotalPrice(totalPrice);
    setTotalCount(totalCount);
    setTopPrice(topPrice);
    setCrustPrice(crustPrice);

    const cartData = {
      items: cartItems,
      totalPrice,
      totalCount,
      topPrice,
      crustPrice
    };

    localStorage.setItem(CART_KEY, JSON.stringify(cartData));
  }, [cartItems]);

  function getCartFromLocalStorage() {
    const storedCart = localStorage.getItem(CART_KEY);
    return storedCart ? JSON.parse(storedCart) : EMPTY_CART;
  }

  const crust = (value) => {
    const val = base.filter(item => item.price === parseFloat(value));
    if (val) {
      cartItems.crust(value);
    }
  };

  const veg = (value) => {
    const val = toppingsVeg.filter(item => item.price === parseInt(value));
    if (val) {
      cartItems.toppings(value);
    }
  };

  const nonVeg = (value) => {
    const val = toppingsNonVeg.filter(item => item.price === parseInt(value));
      cartItems.toppings(value);
  };

  const cheese = (value) => {
    const val = toppingsCheese.filter(item => item.price === parseInt(value));
      cartItems.toppings(val);
  };

  const sum = (item) => {
    return item.reduce((prevValue, curValue) => prevValue + curValue, 0);
  };

  const changeQuantity = (cartItem, newQuantity) => {
    const { food } = cartItem;
    const QuanAndTop = topPrice * newQuantity
    const newPrice = food.price + crustPrice + QuanAndTop
    console.log(QuanAndTop)

    const changedCartItem = {
      ...cartItem,
      quantity: newQuantity,
      price: newPrice
    };

    setCartItems(cartItems.map((item) => (item.food.id === food.id ? changedCartItem : item)));
  };

  const addToCart = (food) => {
    const cartItem = cartItems.find((item) => item.food.id === food.id);
    if (cartItem) {
      changeQuantity(cartItem, cartItem.quantity + 1);
    } else {
      setCartItems([...cartItems, { food, quantity: 1, price: food.price, toppings: 0, crust: 0 }]);
    }
  };

  return (
    <CartContext.Provider
      value={{
        cart: { items: cartItems, totalPrice, totalCount, topPrice, crustPrice },
        removeFromCart,
        changeQuantity,
        addToCart,
        crust,
        veg,
        nonVeg,
        cheese
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export default CartProvider;

export const useCart = () => useContext(CartContext);
