import React from "react";
import classes from "./Customize.module.css";
import { useCart } from "../../hooks/UseCart";
import { base } from "../../Data";

const Customize = ({ food, selectedToppings, setSelectedToppings }) => {
  const { addToCart } = useCart();
  const { veg } = useCart();

  const handleAddToCart = () => {
    addToCart(food, selectedToppings);
  };

  const handleToppingClick = (toppingPrice) => {
    if (!selectedToppings.includes(toppingPrice)) {
      setSelectedToppings([...selectedToppings, toppingPrice]);
      veg(food, toppingPrice);
    }
  };

  const isToppingSelected = (toppingPrice) =>
    selectedToppings.includes(toppingPrice);

  return (
    <div className={classes.main}>
      <div className={classes.bases}>
        <div className={classes.label}>
          <label>
            <img className={classes.base} src="/base.jpg" alt="base" />
          </label>
        </div>
        <div className={classes.container}>
          {base.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price)}
              disabled={isToppingSelected(item.price)}
            >
              {item.base} - ₹{item.price}
            </button>
          ))}
        </div>
      </div>
      <div className={classes.veg}>
        <div>
          <label>
            <img src="/veg.png" alt="veg" />
          </label>
        </div>
        <div>
          {food.toppings.Veg.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price)}
              disabled={isToppingSelected(item.price)}
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
        <div>
          <label>
            <img src="/meat.jpg" alt="meat" />
          </label>
        </div>
        <div>
          {food.toppings.NonVeg.map((item) => (
            <button
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price)}
              disabled={isToppingSelected(item.price)}
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
        <div>
          <label>
            <img src="/cheese.png" alt="cheese" />
          </label>
        </div>
        <div>
          {food.toppings.Cheese.map((item) => (
            <button
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price)}
              disabled={isToppingSelected(item.price)}
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
      </div>
      <div>
        <button onClick={handleAddToCart}>Add Toppings</button>
      </div>
    </div>
  );
};

export default Customize;
