import axios from "axios";

export const createOrder = async ( {data, cart_items, total_price}) => {
    try {
        const { data } = await axios.post('/api/orders/create', { data, cart_items, total_price});
        return data;
    } catch (error) {
        console.error('Error creating order:', error);
        throw error;
    }
};

export const pay = async (paymentId, orderId) => {
    try {
        const { data } = await axios.put('/api/orders/pay', { paymentId, orderId });
        return data;
    } catch (error) {
        console.error('Error updating payment:', error);
        throw error;
    }
};
