import React, { useState, useEffect } from 'react';
import classes from "./Customize.module.css";
import { useCart } from '../../hooks/UseCart';
import { base } from '../../Data';

const Customize = ({ food }) => {
  const { addToCart } = useCart();
  const { veg } = useCart();
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  const handleAddToCart = () => {
    addToCart(food, selectedToppings);
    setIsOpen(false); // Close the Customize page after adding to cart
  };

  const handleToppingClick = (toppingPrice) => {
    if (!selectedToppings.includes(toppingPrice)) {
      setSelectedToppings([...selectedToppings, toppingPrice]);
      veg(food, toppingPrice);
    }
  };

  const isToppingSelected = (toppingPrice) => selectedToppings.includes(toppingPrice);

  useEffect(() => {
    // Listen for clicks outside the Customize container to close it
    const handleClickOutside = (event) => {
      if (isOpen && !event.target.closest(`.${classes.customizeContainer}`)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen]);

  return (
    <div className={`${classes.customizeContainer} ${isOpen ? 'open' : ''}`}>
      <div className={classes.bases}>
        {base.map(item => (
          <button
            key={item.id}
            value={item.price}
            onClick={() => handleToppingClick(item.price)}
            disabled={isToppingSelected(item.price)}
          >
            {item.base} - â‚¹{item.price}
          </button>
        ))}
      </div>
      {/* Other Customize content */}
      <button onClick={handleAddToCart}>Add Toppings</button>
    </div>
  );
};

export default Customize;
