import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import classes from "./UsersPage.module.css"
import { getAll, toggleBlock } from '../../Services/UserService';
import Title from '../../Components/Title/Title';

const UsersPage = () => {
    const [users, setUsers] = useState();
    const { searchTerm } = useParams();

    useEffect(() => {
        loadUsers();
    }, [searchTerm])

    const loadUsers = async () => {
        const users = await getAll(searchTerm);
        setUsers(users)
    }

    const handleToggleBlock = async (userId) => {
      try {
        const response = await toggleBlock(userId);
  
        if (response.success) {
          setUsers((oldUsers) =>
            oldUsers.map((user) =>
              user.id === userId ? { ...user, isBlocked: response.isBlocked } : user
            )
          );
        } else {
          console.error("Failed to toggle block status:", response.error);
          // Handle error state on frontend, e.g., show a message to the user
        }
      } catch (error) {
        console.error("Failed to toggle block status:", error);
        // Handle error state on frontend, e.g., show a message to the user
      }
    };

  return (
    <div className={classes.container}>
      <div className={classes.list}>
        <Title title="Manage Users"/>
        <div className={classes.list_item}>
           <h3>Name</h3>
           <h3>Email</h3>
           <h3>Address</h3>
           <h3>Admin</h3>
           <h3>Actions</h3>
        </div>
        {
            users &&
            users.map(user => 
                <div key={user.id} className={classes.list_item}>
                   <span>{user.name}</span>
                   <span>{user.email}</span>
                   <span>{user.address}</span>
                   <span>{user.isAdmin ? '✅' : '❌'}</span>
                   <span className={classes.actions}>
                     <Link to={'/admin/editUser/' + user.id}>
                       Edit
                     </Link>
                     {
                        <Link onClick={() => handleToggleBlock(user.id)}>
                          {user.isBlock ? 'Unblock' : 'Block'}
                        </Link>
                     }
                   </span>
                </div>
            )
        }
      </div>
    </div>
  )
}

export default UsersPage
