import React, { useState } from "react";
import classes from "./Customize.module.css";
import { useCart } from "../../hooks/UseCart";
import { base } from "../../Data";
import CartPage from "../../Pages/Cart/CartPage";



const Customize = ({ food, selectedToppings, setSelectedToppings, showCustomize}) => {
  const { veg } = useCart();
  const [topping, setTopping] = useState({})

  const handleToppingClick = (toppingPrice, toppingName, food) => {
    setTopping((prevTopping) => {
      const currentToppings = prevTopping[food.id] ? [...prevTopping[food.id]] : []; // Current toppings for the specific food.id
      const updatedToppings = currentToppings.includes(toppingName)
        ? currentToppings.filter((item) => item !== toppingName) // Remove topping if already selected
        : [...currentToppings, toppingName]; // Add topping if not selected

      return {
        ...prevTopping,
        [food.id]: updatedToppings, // Update toppings for the specific food.id
      };
    });

    setSelectedToppings((prevSelected) => {
      if (!prevSelected.includes(toppingPrice)) {
        return [...prevSelected, toppingPrice]; // Add toppingPrice to selectedToppings
      } else {
        return prevSelected.filter((item) => item !== toppingPrice); // Remove toppingPrice from selectedToppings
      }
    });

    veg(food, toppingPrice); // Call veg function with parameters
  };

  console.log(topping)

  console.log(selectedToppings)

  const isToppingSelected = (toppingPrice) =>
    selectedToppings && selectedToppings.includes(toppingPrice);

  return (
    <div className={`${showCustomize === false ? classes.back : classes.main}`}>
      <div className={classes.bases}>
        <div className={classes.label}>
          <label>
            <img className={classes.pimg} src="/base.jpg" alt="base" />
          </label>
        </div>
        <div className={classes.container}>
          {base.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price,item.base,food)}
              disabled={isToppingSelected(item.price)} 
            >
              {item.base} - ₹{item.price}
            </button>
          ))}
        </div>
      </div>
      <div className={classes.veg}>
        <div className={classes.label}>
          <label>
            <img className={classes.pimg} src="/veg.png" alt="veg" />
          </label>
        </div>
        <div>
          {food.toppings.Veg.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price,item.topping,food)}
              disabled={isToppingSelected(item.price)} 
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
        <div className={classes.label}>
          <label>
            <img className={classes.pimg} src="/meat.jpg" alt="meat" />
          </label>
        </div>
        <div>
          {food.toppings.NonVeg.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price,item.topping,food)}
              disabled={isToppingSelected(item.price)} 
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
        <div className={classes.label}>
          <label>
            <img className={classes.pimg} src="/cheese.png" alt="cheese" />
          </label>
        </div>
        <div>
          {food.toppings.Cheese.map((item) => (
            <button
              className={classes.btn}
              key={item.id}
              value={item.price}
              onClick={() => handleToppingClick(item.price,item.topping,food)}
              disabled={isToppingSelected(item.price)} 
            >
              {item.topping} - ₹{item.price}
            </button>
          ))}
        </div>
        <CartPage topping={topping}/>
      </div>
      <div>
      </div>
    </div>
  );
};

export default Customize;
