import React, { useState, useEffect } from 'react';
import classes from "./Checkoutpage.module.css";
import { useCart } from '../../hooks/UseCart';
import { useAuth } from '../../hooks/UseAuth';
import Title from '../../Components/Title/Title';
import { createOrder } from '../../Services/OrderService';
import OrderItemsList from '../../Components/OrderItemsList/OrderItemsList';
import Map from '../../Components/Map/Map';
import PaypalButton from '../../Components/PayPalButton/PaypalButton';
import Input from '../../Components/Input/Input';

const CheckoutPage = () => {
  

  const { cart, clearCart } = useCart();
  const { user, address } = useAuth();
  const [ User, setUser ] = useState(null);
  const [ addressState, setAddressState ] = useState(null);

  const [order, setOrder] = useState({
    ...cart,
    total_price: cart.totalPrice,
  });

  useEffect(() => {
    const cart_items = cart.items.map(item => ({
      item_name: item.food.name,
      toppings: item.toppings,
      price: item.price,
      quantity: item.quantity,
    }));

    setOrder({ ...order, cart_items, total_price: cart.totalPrice });
  }, [cart]);


  const handleOrderCreation = async () => {
    try {
      const createdOrder = await createOrder({
        name: User || user,
        address: addressState || address,
        cart_items: order.cart_items,
        total_price: order.total_price,
      });
      clearCart();
      return createdOrder.orderId;
    } catch (error) {
      console.error('Error creating order:', error);
      throw error;
    }
  };


  return (
    <>
      <div className={classes.container}>
        <div className={classes.content}>
          <Title title="Order Form" fontSize="1.6rem" />
            <div className={classes.inputs}>
                <Input
                 defaultValue={user}
                 type="text"
                 label="User"
                 value
                 onChange={(e) => setUser(e.target.value)}
                 />
                <Input
                 defaultValue={address}
                 type="text"
                 label="Address"
                 onChange={(e) => setAddressState(e.target.value)}
                 />
            </div>
            <OrderItemsList order={order} />
        </div>
        <div>
          <Title title="Choose Your Location" fontSize="1.6rem" />
          <Map 
             location={order.addressLatLng} 
             onChange={latlng => {
              setOrder({ ...order, addressLatLng: latlng });
             }} />
        </div>
        <div className={classes.buttons_container}>
          <div className={classes.buttons}>
            <PaypalButton order={order} handleOrderCreation={handleOrderCreation} />
          </div>
        </div>
      </div>
    </>
  );
};

export default CheckoutPage;
