import React, { useEffect, useState } from 'react'
import { useCart } from '../../hooks/UseCart'
import classes from "./Checkoutpage.module.css"
import { useAuth } from '../../hooks/UseAuth';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { toast } from 'react-toastify';
import { createOrder } from '../../Services/OrderService';
import Title from '../../Components/Title/Title';
import Input from '../../Components/Input/Input';
import Map from '../../Components/Map/Map';
import Button from '../../Components/Button/Button';
import OrderItemsList from '../../Components/OrderItemsList/OrderItemsList';

const CheckoutPage = () => {
  const { cart } = useCart();
  const { user, address } = useAuth();
  const navigate= useNavigate();
  const [name, setName] = useState(null);
  const [addressState, setAdressState] = useState(null);
  const [order, setOrder] = useState({ ...cart, cart_items: cart.items });
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm();

 

  useEffect(() => {
    const cart_items = cart.items.map(item => ({
      item_name: item.food.name,
      toppings: item.toppings,
      price: item.price,
      quantity: item.quantity,
    }));
    setOrder({ ...order, cart_items, total_price: order.total_price });
  }, [cart]);

  const submit = async data => {
    if(!order.addressLatLng) {
      toast.warning('Please select your location on the map');
      return;
    }
    await createOrder({ cart_items: order.cart_items, name: user, address: address, total_price: order.total_price });
    navigate('/payment');
  }

  return (
    <>
     <form 
        onSubmit={handleSubmit(submit)}
        className={classes.container}>
        <div className={classes.content}>
            <Title title="Order Form" fontSize="1.6rem" />
            <div className={classes.inputs}>
               <Input
                 defaultValue={user}
                 label="Name"
                 value={name}
                 onChange={(e) => setName(e.target.value)}
                 {...register('name')}
                 error={errors.name}
                 />
               <Input
                 defaultValue={address}
                 label="Address"
                 value={addressState}
                 {...register('address')}
                 error={errors.address}
                 />
            </div>
            <OrderItemsList order={order} />
        </div>
        <div>
          <Title title="Choose Your Location" fontSize="1.6rem"/>
          <Map 
            location={order.addressLatLng} 
            onChange={(latlng) => {
              setOrder({...order, addressLatLng: latlng})
            }} />
        </div>
        <div className={classes.buttons_container}>
            <div className={classes.buttons}>
               <Button 
                 type="submit"
                 text="Go To Payment"
                 width="100%"
                 height="3rem"
                 />
            </div>
        </div>
     </form>
    </>
  )
}

export default CheckoutPage
