import { PayPalButtons, PayPalScriptProvider, usePayPalScriptReducer } from '@paypal/react-paypal-js';
import { pay } from '../../Services/OrderService';
import React, { useEffect } from 'react';
import { useCart } from '../../hooks/UseCart';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import { Loading } from '../../hooks/Loading';

const PaypalButton = ({ order }) => {
  return (
    <PayPalScriptProvider
      options={{
        clientId: 'AQnFPqb3gFs_c_mNNFr4_uJbw9vQEpdU2AAGqj26y4koGgdst2C_wmrE_f2m44q3oboQapoZR8reXQRz'
      }}
    >
      <Buttons order={order} />
    </PayPalScriptProvider>
  );
};

export default PaypalButton;

function Buttons({ order }) {
  const { clearCart } = useCart();
  const navigate = useNavigate();
  const [{ isPending }] = usePayPalScriptReducer();
  const { showLoading, hideLoading } = Loading();

  useEffect(() => {
    isPending ? showLoading() : hideLoading();
  }, [isPending, showLoading, hideLoading]);

  const createOrder = (data, actions) => {
    return actions.order.create({
      purchase_units: [{
        amount: {
          currency_code: 'USD',
          value: order.total_price,
        },
      }],
    });
  };

  const onApprove = async (data, actions) => {
    try {
      const payment = await actions.order.capture();
      const orderId = await createOrder();
      console.log(orderId)
      await pay(payment.id, orderId);
      clearCart();
      toast.success('Payment Saved Successfully', 'Success');
      navigate('/track/' + orderId);
    } catch (error) {
      toast.error('Payment Saved Failed', 'Error');
    }
  };

  const onError = (err) => {
    toast.error('Payment Failed', 'Error');
  };

  return (
    <PayPalButtons
      createOrder={createOrder}
      onApprove={onApprove}
      onError={onError}
    />
  );
}
