import React from 'react';
import classes from "./Customize.module.css";
import { useCart } from '../../hooks/UseCart';
import { base,toppings } from '../../Data';



const Customize = ({food}) => {
  const { addToCart } = useCart();
  const { crust, veg, nonVeg, cheese } = useCart();
  
  const handleAddToCart = () => {
    addToCart(food);
  };
  
  return (
    <div className={classes.main}>
      <div className={classes.bases}>
        <label>Crust </label>
        <select value={0} onChange={(e) => crust(e.target.value)}>
          {base.map(opt => (
             <option key={opt.base} value={opt.price}>{opt.base}</option>
          ))}
        </select>
       </div>
       <div className={classes.veg}>
        <label>Veg </label>
        {toppingsVeg.map(item => (
          <button key={item.topping} value={item.price} onClick={(e) => veg(e.target.value)}>{item.topping}</button>
        ))}
        </div>
       <div className={classes.nonVeg}>
        <label>NonVeg </label>
        {toppingsNonVeg.map(item => (
          <button key={item.topping} value={item.price} onClick={(e) => nonVeg(e.target.value)}>{item.topping}</button>
        ))}
       </div>
       <div className={classes.cheese}>
        <label>Cheese </label>
        {toppingsCheese.map(item => (
              <button key={item.topping} value={item.price} onClick={(e) => cheese(e.target.value)}>{item.topping}</button>
        ))}
       </div>
       <div>
          <button onClick={()=>handleAddToCart(food)}>Add Toppings</button>
       </div>
    </div>
  );
};

export default Customize;
