import React, { useState } from 'react';
import classes from  "./SignUp.module.css"
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';
import { useAuth } from '../../hooks/UseAuth';
import Title from '../../Components/Title/Title';
import Input from '../../Components/Input/Input';
import Button from '../../Components/Button/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEye, faEyeSlash, faEnvelope, faAddressCard } from '@fortawesome/free-solid-svg-icons';

const SignUp = () => {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const { register: signup } = useAuth();

    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
        setShowPassword(prevShowPassword => !prevShowPassword);
    };

    const onSubmit = async (data) => {
        try {
            await signup(data.name, data.email, data.password);
        } catch (error) {
            console.error('Registration failed:', error);
        }
    };

    return (
        <div className={classes.container}>
            <div className={classes.details}>
            <Title title="Sign Up"/>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <Input
                        type="text"
                        label="Name"
                        {...register('name', { required: true })}
                        error={errors.name}
                    />
                    <FontAwesomeIcon icon={faUser} className={classes.icon1} />
                </div>
                <div>
                    <Input
                        type="email"
                        label="Email"
                        {...register('email', {
                            required: true,
                            pattern: {
                                value: /^[\w-.]+@([\w-]+\.)+[\w-]{2,63}$/i,
                                message: 'Email is not valid'
                            }
                        })}
                        error={errors.email}
                    />
                    <FontAwesomeIcon icon={faEnvelope} className={classes.icon2} />
                </div>
                <div>
                    <Input
                        type={showPassword ? "text" : "password"}
                        label="Password"
                        {...register('password', { required: true })}
                        error={errors.password}
                    />
                     <FontAwesomeIcon 
                            icon={showPassword ? faEyeSlash : faEye} 
                            className={classes.icon3} 
                            onClick={togglePasswordVisibility} 
                    />
                </div>
                <div>
                    <Input
                        type="text"
                        label="A"
                        {...register('name', { required: true })}
                        error={errors.name}
                    />
                    <FontAwesomeIcon icon={faAddressCard} className={classes.icon4} />
                </div>
                <Button type="submit" text="Sign Up"/>
                <div className={classes.register}>
                        <p>Already have an account? <Link to="/login">login</Link></p>
                </div>
            </form>
            </div>
        </div>
    );
};

export default SignUp;
