import React, { useEffect, useRef } from 'react';
import classes from "./"
import { useCart } from '../../hooks/UseCart';
import { base } from '../../Data';

const Customize = ({ onClose }) => {
  const { crust, veg, nonVeg, cheese } = useCart();
  const myRef = useRef(null);

  const handleClickOutside = (e) => {
    if (myRef.current && !myRef.current.contains(e.target)) {
      onClose();
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  });

  return (
    <div className={classes.overlay}>
      <div ref={myRef} className={classes.main}>
        <div className={classes.bases}>
          <label>Crust </label>
          <select value={0} onChange={(e) => crust(e.target.value)}>
            {base.map((opt) => (
              <option key={opt.base} value={opt.price}>
                {opt.base}
              </option>
            ))}
          </select>
        </div>
        <div className={classes.veg}>
          <label>Veg </label>
          <button value={10} onClick={(e) => veg(e.target.value)}>
            Bellpeppers
          </button>
          <button value={20} onClick={(e) => veg(e.target.value)}>
            Onions
          </button>
          <button value={30} onClick={(e) => veg(e.target.value)}>
            Mushrooms
          </button>
        </div>
        <div className={classes.nonVeg}>
          <label>NonVeg </label>
          <button value={40} onClick={(e) => nonVeg(e.target.value)}>
            Pepperoni
          </button>
          <button value={50} onClick={(e) => nonVeg(e.target.value)}>
            Sausage
          </button>
          <button value={80} onClick={(e) => nonVeg(e.target.value)}>
            Chicken
          </button>
        </div>
        <div className={classes.cheese}>
          <label>Cheese </label>
          <button value={30} onClick={(e) => cheese(e.target.value)}>
            Mozzarella
          </button>
          <button value={25} onClick={(e) => cheese(e.target.value)}>
            Cheddar
          </button>
          <button value={45} onClick={(e) => cheese(e.target.value)}>
            Feta
          </button>
        </div>
        <div>
          <button onClick={onClose}>Add Toppings</button>
        </div>
      </div>
    </div>
  );
};

export default Customize;
