import React, { useState, useEffect } from 'react';
import classes from "./Map.module.css";
import 'leaflet/dist/leaflet.css';
import {
    MapContainer,
    TileLayer,
    Marker,
    Popup,
    useMapEvents,
} from "react-leaflet";
import { toast } from 'react-toastify';
import { getAddressFromLatLng } from './Geoloc';

const Map = ({ readOnly, location, onChange }) => {
  return (
    <div className={classes.container}>
       <MapContainer
         className={classes.map}
         center={[0,0]}
         zoom={1}
         dragging={!readOnly}
         touchZoom={!readOnly}
         doubleClickZoom={!readOnly}
         scrollWheelZoom={!readOnly}
         boxZoom={!readOnly}
         keyboard={!readOnly}
         attributionControl={false}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
            <FindButtonAndMarker
              readOnly={readOnly}
              location={location}
              onChange={onChange}
              />
         </MapContainer>
    </div>
  )
}

export default Map

function FindButtonAndMarker({ readOnly, location, onChange }) {
  const [position, setPosition] = useState(location);
  const map = useMap();

  useEffect(() => {
    if (map && position) {
      map.setView(position, 13);
      onChange(position);
    }
  }, [position, map]);

  const updateAddress = async (latLng) => {
    try {
      const address = await getAddressFromLatLng(latLng.lat, latLng.lng);
      onChange(latLng, address);
    } catch (error) {
      toast.error("Failed to fetch address");
    }
  };

  useMapEvents({
    click(e) {
      if (!readOnly) {
        setPosition(e.latlng);
        updateAddress(e.latlng);
      }
    },
    locationfound(e) {
      setPosition(e.latlng);
      map.flyTo(e.latlng, 13);
      updateAddress(e.latlng);
    },
    locationerror(e) {
      toast.error(e.message);
    },
  });

  return (
    <>
      {!readOnly && (
        <button
          type="button"
          className={classes.find_location}
          onClick={() => map.locate()}
        >
          Find My Location
        </button>
      )}
      {position && (
        <Marker
          eventHandlers={{
            dragend: (e) => {
              setPosition(e.target.getLatLng());
              updateAddress(e.target.getLatLng());
            },
          }}
          position={position}
          draggable={!readOnly}
        >
          <Popup>Shipping Location</Popup>
        </Marker>
      )}
    </>
    )
}
