import React, { createContext, useContext, useEffect, useState } from 'react';

const CartContext = createContext(null);
const CART_KEY = 'cart';
const EMPTY_CART = {
  items: [],
  totalPrice: 0,
  totalCount: 0
};

const CartProvider = ({ children }) => {
  const initCart = getCartFromLocalStorage();
  const [cartItems, setCartItems] = useState(initCart.items);
  const [totalPrice, setTotalPrice] = useState(initCart.totalPrice);
  const [totalCount, setTotalCount] = useState(initCart.totalCount);
  const [toppings, setToppings] = useState([]);

  const removeFromCart = (foodId, toppingsToRemove) => {
    const filteredCartItems = cartItems.filter((item) => {
      // Check if the food ID matches
      if (item.food.id === foodId) {
        // If toppings are provided, check if they match
        if (toppingsToRemove && toppingsToRemove.length > 0) {
          // Check if the toppings for this item match the toppings to remove
          const itemToppings = toppings[item.food.id] || [];
          const sameToppings = arraysEqual(itemToppings, toppingsToRemove);
          return !sameToppings; // Return false to keep items with different toppings
        }
        // If no toppings provided, remove this specific instance
        return false;
      }
      // Keep items with different food IDs
      return true;
    });
  
    setCartItems(filteredCartItems);
  
    // Remove toppings related to the removed pizza
    if (toppingsToRemove && toppingsToRemove.length > 0) {
      setToppings((prevToppings) => {
        const updatedToppings = { ...prevToppings };
        delete updatedToppings[foodId];
        return updatedToppings;
      });
    }
  };

  
  
  

  useEffect(() => {
    const totalPrice = sum(cartItems.map((item) => item.price));
    const totalCount = sum(cartItems.map((item) => item.quantity));
    setTotalPrice(totalPrice);
    setTotalCount(totalCount);

    const cartData = {
      items: cartItems,
      totalPrice,
      totalCount
    };

    localStorage.setItem(CART_KEY, JSON.stringify(cartData));
  }, [cartItems]); 

  function getCartFromLocalStorage() {
    const storedCart = localStorage.getItem(CART_KEY);
    return storedCart ? JSON.parse(storedCart) : EMPTY_CART;
  }

  const veg = (food, value) => {
    console.log("Food: ", food);
    if (food) {
      setToppings((prevToppings) => ({
        ...prevToppings,
        [food.id]: prevToppings[food.id] ? prevToppings[food.id] + Number(value) : Number(value)
      }));
    }
  };

  console.log(toppings);

  const sum = (item) => {
    return item.reduce((prevValue, curValue) => prevValue + curValue, 0);
  };

  const changeQuantity = (cartItem, newQuantity) => {
    const { food } = cartItem;
    const price = toppings[food.id]
      ? (food.price + toppings[food.id]) * newQuantity
      : food.price * newQuantity;

    const changedCartItem = {
      ...cartItem,
      quantity: newQuantity,
      price: price
    };

    setCartItems(cartItems.map((item) => (item.food.id === food.id ? changedCartItem : item)));
  };

  const addToCart = (food, selectedToppings = []) => {
    const existingCartItem = cartItems.find((item) => item.food.id === food.id);
  
    // If the same pizza is already in the cart, add a new instance with the selected toppings
    if (existingCartItem) {
      const newCartItem = {
        food,
        quantity: 1,
        price: calculatePrice(food.price, selectedToppings)
      };
  
      setCartItems([...cartItems, newCartItem]);
    } else {
      // Otherwise, add the pizza with selected toppings or without toppings
      const newCartItem = {
        food,
        quantity: 1,
        price: calculatePrice(food.price, selectedToppings)
      };
  
      setCartItems([...cartItems, newCartItem]);
    }
  };

  const calculatePrice = (basePrice, selectedToppings) => {
    // Calculate the total price by adding the base price and the prices of selected toppings
    const toppingsPrice = selectedToppings.reduce((total, toppingPrice) => total + toppingPrice, 0);
    return basePrice + toppingsPrice;
  };
  
  

  return (
    <CartContext.Provider
      value={{
        cart: { items: cartItems, totalPrice, totalCount },
        removeFromCart,
        changeQuantity,
        addToCart,
        veg
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export default CartProvider;

export const useCart = () => useContext(CartContext);
