import React, { useEffect, useState } from 'react';
import classes from "./orderPage.module.css"
import { useAuth } from '../../hooks/UseAuth';
import { currentUser } from '../../Services/OrderService';
import Title from '../../Components/Title/Title';
import DateTime from '../../Components/DateTime/DateTime';
import { Link } from 'react-router-dom';
import Price from '../../Components/Price/Price';
import Button from '../../Components/Button/Button';

const OrdersPage = () => {
  const { email } = useAuth();
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getNewOrder = async () => {
      try {
        const data = await currentUser(email);
        setOrders(data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    getNewOrder();
  }, [email]);

  const removeOrder = (orderId) => {
       const filter = orders.filter(item => item.id !== orderId)
       set
  }

  return (
    <div className={classes.container}>
        <Title title="Orders" margin="1.5rem 0 0 .2rem" fontSize="1.9rem" />

        {orders && 
           orders.map(order => (
            <div key={order.id} className={classes.order_summary}>
               <div className={classes.header}>
                  <span>{order.id}</span>
                  <span>
                    <DateTime date={order.created_at} />
                  </span>
                  <span>
                    {order.status}
                  </span>
               </div>
               <div className={classes.delivery}>
                {order.name} ,{order.address}
              </div>
               <div className={classes.items}>
                   {
                    order.cart_items.map(item => 
                      <div className={classes.item}>
                         <Link key={item.item_id} to={`/food/${item.item_id}`}>
                          <img src={`/pizzas/${item.item_url}`} alt={item.item_name} />
                          <p key={item.item_id}>Quantity: {item.quantity}</p>
                      </Link>
                      <p key={item.item_id}> {item.item_name} {item.toppings ? "-" : ""} {item.toppings ? item.toppings.join(", ") : ""}</p>
                      </div>
                      
                    )
                   }
               </div>
               <div>
                <div className={classes.footer}>
                  <div>
                    Order again
                  </div>
                  <div>
                    <Button text="remove" width="5rem" height="1.5rem" fontSize="1rem" backgroundColor="#F23B2D" onClick={removeOrder(order.id)}> remove </Button>
                  </div>
                  <span className={classes.price}>
                        <Price price={order.total_price} />
                  </span>
                </div>
                </div>
            </div>
           ))
        }
    </div>
  );
};

export default OrdersPage;
