import axios from 'axios';
import { useParams } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/users';
const {id, token} = useParams()


export const register = async (name, email, password, address) => {
    await axios.post(`${API_URL}/register`, { name, email, password, address });
};

export const login = async (email, password) => {
    try {
        const { data } = await axios.post(`${API_URL}/login`, { email, password });
         
        const { token, name, address, emailId } = data;

        
        document.cookie = `token=${token}; max-age=${30 * 24 * 60 * 60}; path=/;`;
        return {name,address,emailId};
        
    } catch (error) {
        console.error('Error logging in:', error);
        throw error;
    }
};

export const logout = async () => {
    try {
        await axios.post(`${API_URL}/logout`);
        document.cookie = `token=; max-age=${30 * 24 * 60 * 60}; path=/;`;

        document.cookie = `token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
    } catch (error) {
        console.error('Error logging out:', error);
        throw error;
    }
};

const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
    return null;
};

export const getToken = () => {
    return getCookie('token');
};

export const forgotPass = async (email) => {
    try {
        await axios.post(`${API_URL}/forgot-password`, { email });   
    } catch (error) {
        console.error('Failed to send mail:', error);
        throw error;
    }
};

export const resetPass = async (password) => {
    try {
        await axios.post(`${API_URL}/reset-password`, { password });   
    } catch (error) {
        console.error('Failed to send mail:', error);
        throw error;
    }
};
