import React, { useEffect, useState } from 'react';
import classes from "./orderPage.module.css";
import { useAuth } from '../../hooks/UseAuth';
import { currentUser, DeleteOrder } from '../../Services/OrderService';
import Title from '../../Components/Title/Title';
import DateTime from '../../Components/DateTime/DateTime';
import { Link } from 'react-router-dom';
import Price from '../../Components/Price/Price';
import Button from '../../Components/Button/Button';

const OrdersPage = () => {
  const { email } = useAuth();
  const [orders, setOrders] = useState([]);
  const { setSelectedOrderId } = useAppContext(); 
  const [error, setError] = useState(null);

  useEffect(() => {
    const getNewOrder = async () => {
      try {
        const data = await currentUser(email);
        setOrders(data);
      } catch (error) {
        console.error('Error fetching orders:', error);
        setError('Failed to load orders');
      }
    };

    getNewOrder();
  }, [email]);

  const handleDeleteOrder = async (orderId) => {
    try {
      await DeleteOrder(orderId);
      setOrders((prevOrders) => prevOrders.filter(order => order.id !== orderId));
    } catch (error) {
      console.error('Error deleting order:', error);
      setError('Failed to delete order');
    }
  };

  return (
    <div className={classes.container}>
      <Title title="Orders" margin="1.5rem 0 0 .2rem" fontSize="1.9rem" />
      {error && <p className={classes.error}>{error}</p>}
      {orders && 
        orders.map(order => (
          <div key={order.id} className={classes.order_summary}>
            <div className={classes.header}>
              <span>{order.id}</span>
              <span>
                <DateTime date={order.created_at} />
              </span>
              <span>
                {order.status}
              </span>
            </div>
            <div className={classes.delivery}>
              {order.name}, {order.address}
            </div>
            <div className={classes.items}>
              {order.items.map((item, index) => (
                <div key={index} className={classes.item}>
                  <Link to={`/food/${item.food.id}`}>
                    <img src={`/pizzas/${item.food.imageUrl}`} alt={item.food.name} />
                    <p>Quantity: {item.quantity}</p>
                  </Link>
                  <p>{item.food.name} {item.food.toppings ? "-" : ""} {item.food.toppings ? item.food.toppings.join(", ") : ""}</p>
                </div>
              ))}
            </div>
            <div className={classes.footer}>
              <div>
              <Link
                  to={{
                    pathname: '/checkout',
                    state: {orderId: order.id} 
                  }}
                >
                  <Button text="Order Again" width="7rem" height="1.5rem" fontSize="1rem" backgroundColor="#F23B2D" />
                </Link>
              </div>
              <div>
                <Button text="Cancel Order" width="7rem" height="1.5rem" fontSize="1rem" backgroundColor="#F23B2D" onClick={() => handleDeleteOrder(order.id)} />
              </div>
              <span className={classes.price}>
                <Price price={order.totalPrice} />
              </span>
            </div>
          </div>
        ))
      }
    </div>
  );
};

export default OrdersPage;
