import React, { useState } from 'react';
import classes from "./Customize.module.css";
import { useCart } from '../../hooks/UseCart';
import { base} from '../../Data';



const Customize = ({food}) => {
  const { addToCart } = useCart();
  const {veg} = useCart();
  const [selectedToppings, setSelectedToppings] = useState([]);
  
  const handleAddToCart = () => {
    addToCart(food);
  };
  
  const handleToppingClick = (topping) => {
    if (!selectedToppings.includes(topping.id)) {
      setSelectedToppings([...selectedToppings, topping.id]);
      veg(food, food.toppings.price); // Pass both food item and topping price to veg function
    }
  };

  const isToppingSelected = (topping) => selectedToppings.includes(topping.id);

  return (
    <div className={classes.main}>
      <div className={classes.bases}>
        <label>Bases</label>
        <select>
         {base.map(item => (
          <option key={item.id} value={item.price}>{item.base} - ₹{item.price}</option>
         ))
         }
         </select>
      </div>
      <div className={classes.veg}>
        <label>Veg </label>
        {food.toppings.Veg.map(item => (
          <button 
            key={item.id} 
            value={item.price} 
            onClick={() => handleToppingClick(item)}
            disabled={isToppingSelected(item)}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
        <label>NonVeg </label>
        {food.toppings.NonVeg.map(item => (
          <button 
            key={item.id} 
            value={item.price} 
            onClick={() => handleToppingClick(item)}
            disabled={isToppingSelected(item)}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
        <label>Cheese </label>
        {food.toppings.Cheese.map(item => (
          <button 
            key={item.id} 
            value={item.price} 
            onClick={() => handleToppingClick(item)}
            disabled={isToppingSelected(item)}
          >
            {item.topping} - ₹{item.price}
          </button>
        ))}
      </div>
      <div>
        <button onClick={handleAddToCart}>Add Toppings</button>
      </div>
    </div>
  );
};

export default Customize;
